import React from 'react';
import { useParams, Link } from 'react-router-dom';
import projectData from '../data/projects';
import '../styles/projects.css';

function ProjectDetailPage() {
  const { id } = useParams();
  const project = projectData.find(p => String(p.id) === String(id));

  if (!project) {
    return (
      <section className="projects-section">
        <div className="container">
          <h2>Project not found</h2>
          <Link to="/projects" className="primary-btn">Back to Projects</Link>
        </div>
      </section>
    );
  }

  return (
    <section className="projects-section">
      <div className="container">
        <div className="section-title">
          <h2>{project.title}</h2>
          <div className="underline"></div>
        </div>

        <div className="project-detail">
          <img src={project.image} alt={project.title} style={{maxWidth: '600px', width: '100%', borderRadius: '8px'}} />

          <div style={{marginTop: '1rem'}}>
            <p>{project.desc}</p>

            <div style={{marginTop: '1rem'}}>
              {project.tags.map((t, i) => (
                <span key={i} style={{marginRight: '0.5rem'}} className="project-tech">{t}</span>
              ))}
            </div>

            <div style={{marginTop: '1.5rem'}}>
              <Link to="/projects" className="secondary-btn">Back</Link>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ProjectDetailPage;
