import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/projects.css";
import projectData from "../data/projects";


const Projects = () => {

  const [filter, setFilter] = useState("All");

  // projectData imported from ../data/projects

  const filteredProjects = filter === "All"
    ? projectData
    : projectData.filter(p => p.category === filter);

  return (
    <section className="projects-section" id="projects">

      <div className="section-title">
        <h2>Projects</h2>
        <div className="underline"></div>
      </div>

      <div className="project-filters">
        <button onClick={() => setFilter("All")}>All</button>
        <button onClick={() => setFilter("Web")}>Web</button>
        <button onClick={() => setFilter("Mobile")}>Mobile</button>
        <button onClick={() => setFilter("ML")}>ML/AI</button>
      </div>

      <div className="projects-grid">
        {filteredProjects.map((p) => (
          <div className="project-card" key={p.id}>

            <img src={p.image} alt={p.title} />

            <div className="project-content">
              <h4>{p.title}</h4>
              <p>{p.desc}</p>

              <div className="project-tags">
                {p.tags.map((tag, i) => (
                  <span key={i}>{tag}</span>
                ))}
              </div>

              <div className="project-actions" style={{marginTop: '1rem'}}>
                <Link to={`/projects/${p.id}`} className="primary-btn">View Project</Link>
              </div>
            </div>

          </div>
        ))}
      </div>

    </section>
  );
};

export default Projects;
