import React, { useState } from "react";
import "../styles/projects.css";

const Projects = () => {

  const [filter, setFilter] = useState("All");

  const projectData = [
    {
      title: "Go Guide",
      category: "Web",
      image: "../ass",
      desc: "A Go Guide is a traveling guide app for the traveller.",
      tags: ["HTML", "CSS", "JavaScript"]
    },
    {
      title: "Resume Builder Web",
      category: "Web",
      image: "/images/project2.png",
      desc: "A Website that make a simple resume for the user.",
      tags: ["React JS"]
    },
    {
      title: "Tracelly - Lost & Found",
      category: "Web",
      image: "/images/project3.png",
      desc: "A Website in which we can report our lost items easily.",
      tags: ["React JS", "Node Express"]
    },
    {
      title: "NB's Gaming Hub",
      category: "Mobile",
      image: "/images/project4.png",
      desc: "I have make this website of simple games.",
      tags: ["React JS"]
    },
    {
      title: "My Portfolio",
      category: "Web",
      image: "/images/project5.png",
      desc: "I have make my own Portfolio website.",
      tags: ["HTML", "CSS", "JavaScript"]
    }
  ];

  const filteredProjects = filter === "All"
    ? projectData
    : projectData.filter(p => p.category === filter);

  return (
    <section className="projects-section" id="projects">

      <div className="section-title">
        <h2>Projects</h2>
        <div className="underline"></div>
      </div>

      <div className="project-filters">
        <button onClick={() => setFilter("All")}>All</button>
        <button onClick={() => setFilter("Web")}>Web</button>
        <button onClick={() => setFilter("Mobile")}>Mobile</button>
        <button onClick={() => setFilter("ML")}>ML/AI</button>
      </div>

      <div className="projects-grid">
        {filteredProjects.map((p, index) => (
          <div className="project-card" key={index}>

            <img src={p.image} alt={p.title} />

            <div className="project-content">
              <h4>{p.title}</h4>
              <p>{p.desc}</p>

              <div className="project-tags">
                {p.tags.map((tag, i) => (
                  <span key={i}>{tag}</span>
                ))}
              </div>
            </div>

          </div>
        ))}
      </div>

    </section>
  );
};

export default Projects;
